import React from "react";
import Header from "../components/Header.jsx";
import { fetchNfts } from "../lib/Nfts";
import { useEffect, useState } from 'react';
import "./NftGallery.css"
import NftCard from '../components/NftCard'




function NftGallery() {
    const [data, setData] = useState(null)
    const [isLoading, setLoading] = useState(false)

    useEffect(() => {
        setLoading(true)
        fetchNfts().then((res) => res.json())
        .then((data) => {
            setData(data)
            setLoading(false)
        })
    }, []);

    if (isLoading) return <p>Loading...</p>
    if(!data) return <p>No data available</p>
    const nfts = data.nfts;
    const sortedNfts = nfts.filter(nft => nft.metadata) 

    // console.log(sortedNfts)

    return (
        <>

       <div className="container">
        <h1>NFT Gallery</h1>
            <div className="nft-container">


            {sortedNfts.map((nft) => {


                    let calc = nft.metadata.name.split("(?=#)")
                    let title = calc[0];
                    let num = calc[1];


                return(

                    <NftCard key={nft.mint_address} url={nft.cached_file_url} title={title} desc={nft.metadata.description} number={num} />
                    // <div key={nft.metadata_url} >
                    //     <img src={nft.cached_file_url} alt={nft.collection_id} />
                    // </div>
                    )
                })}
            </div>
                </div>

            </>

        )

}

export default NftGallery;
