import React from "react";
import { BallTriangle } from "react-loader-spinner";
import { continuedFetchNfts, fetchNfts } from "../lib/Nfts";
import { useEffect, useState } from "react";
import "./NftGallery.css";
import NftCard from "../components/NftCard";

function NftGallery() {
  const [myData, setMyData] = useState(null);
  const [isLoading, setLoading] = useState(false);
  const [cont, setCont] = useState(false);
  const [contData, setContData] = useState(null);

  useEffect(() => {
    setLoading(true);
    fetchNfts()
      .then((res) => res.json())
      .then((data) => {
        setMyData(data);

        if(data.continuation != null) {
          setCont(true)
        } 

        if(cont === true) {
          continuedFetchNfts()
          .then((res) => res.json())
          .then((data) => {
            setContData(data)

            setCont(false)
          })
        }

      });



  }, []);

  if (isLoading)
    return (
      <>
        <div className="loading-container">
          <div className="loading-spinner-container">
            <BallTriangle
              height={100}
              width={100}
              radius={5}
              color="blue"
              ariaLabel="ball-triangle-loading"
              wrapperClass={{}}
              wrapperStyle=""
              visible={true}
            />
          </div>
          <div className="loading-text-container">
            <p>Fetching NFTs for you..</p>
          </div>
        </div>
      </>
    );
  if (!myData) return <p>No data available</p>;
  const nfts = myData.nfts;
  // const sortedNfts = nfts.filter((nft) => nft.metadata);

  // console.log(sortedNfts)

  return (
    <>
      <div className="container">
        <h1>NFT Gallery</h1>
        <div className="nft-container">
          {/* {sortedNfts.map((nft) => {
            let str = nft.metadata.name;
            let title = str.slice(0, -5);

            let num = str.substring(str.length - 5);

            return (
              <NftCard
                key={nft.mint_address}
                url={nft.cached_file_url}
                title={title}
                desc={nft.metadata.description}
                number={num}
                link={nft.cached_file_url}
              />
              // <div key={nft.metadata_url} >
              //     <img src={nft.cached_file_url} alt={nft.collection_id} />
              // </div>
            );
          })} */}
        </div>
      </div>
    </>
  );
}

export default NftGallery;
