export const fetchNfts = async () => {
  const url = "https://api.nftport.xyz/v0/solana/accounts/558acsgeuPDFQwdCTM8LWjgyiiZCPyoS9iXts1KAWRCW?include=metadata";
fetchAllPages(url).then(console.log).catch(console.warn)
};

export const continuedFetchNfts = async () => {
  return fetch(
    "https://api.nftport.xyz/v0/solana/accounts/558acsgeuPDFQwdCTM8LWjgyiiZCPyoS9iXts1KAWRCW?continuation=Mg%3D%3D",
    {
      headers: {
        Authorization: process.env.REACT_APP_AUTH_CODE,
      },
    }
  );
};

const addToken = (base, token) => {
  const url = new URL(base);
  const searchParams = new URLSearchParams(url.search);
  searchParams.set("continuation", token);
  url.search = searchParams;
  return url.toString();
};

const fetchAllPages = (url) => {
  fetch(url, 
    {
        headers: {
          Authorization: process.env.REACT_APP_AUTH_CODE,
        },
     } )
    .then((res) => res.json())
    .then(({ items, continuation: token }) =>
      token
        ? fetchAllPages(addToken(url, token)).then((newItems) => [
            ...items,
            ...newItems,
          ])
        : items
    );
};
